import React, { useState, useEffect } from 'react';
import Typical from 'react-typical';
import './TypingEffect.css';

const TypingEffect = ({ errorCount, elapsedTime, errorRate, lastScore, onNextLevel, onExitGame }) => {
  const [userInput, setUserInput] = useState('');
  const [displayedMessages, setDisplayedMessages] = useState([]);

  const messages = [
    `錯誤字數：${errorCount}`,
    `花費時間：${elapsedTime} 秒`,
    `錯誤率：${errorRate}%`,
    `本次得分：${lastScore} 分`,
    '是否進入下一關？請輸入「是」或「否」'
  ];

  useEffect(() => {
    let currentMessageIndex = 0;
    let currentCharIndex = 0;
    const interval = setInterval(() => {
      if (currentMessageIndex < messages.length) {
        if (currentCharIndex < messages[currentMessageIndex].length) {
          setDisplayedMessages((prevMessages) => {
            const updatedMessages = [...prevMessages];
            if (updatedMessages[currentMessageIndex]) {
              updatedMessages[currentMessageIndex] += messages[currentMessageIndex][currentCharIndex];
            } else {
              updatedMessages[currentMessageIndex] = messages[currentMessageIndex][currentCharIndex];
            }
            return updatedMessages;
          });
          currentCharIndex++;
        } else {
          currentMessageIndex++;
          currentCharIndex = 0;
        }
      } else {
        clearInterval(interval);
      }
    }, 100);

    return () => clearInterval(interval);
  }, [messages]);

  const handleInputChange = (e) => {
    setUserInput(e.target.value);
  };

  const handleUserInputSubmit = () => {
    if (userInput.trim() === '是') {
      onNextLevel();
    } else if (userInput.trim() === '否') {
      onExitGame();
    } else {
      alert('請輸入有效的選項：「是」或「否」');
    }
  };

  return (
    <div className="typing-effect-container">
      <div className="message-bubble">
        <p className="sender">系統</p>
        {displayedMessages.map((msg, index) => (
          <p key={index}>{msg}</p>
        ))}
      </div>
      <div className="user-input-container">
        <input
          type="text"
          value={userInput}
          onChange={handleInputChange}
          placeholder="請輸入「是」或「否」"
        />
        <button onClick={handleUserInputSubmit}>提交</button>
      </div>
    </div>
  );
};

export default TypingEffect;
