// src/App.js
import React, { useState } from 'react';
import GamePage from './GamePage';
import TypingGame from './TypingGame';
import dataEng from './dataEng.json';
import dataCh from './dataCh.json';
import './App.css';

function App() {
  const [gameStarted, setGameStarted] = useState(false);
  const [gameText, setGameText] = useState('');
  const [gameIndex, setGameIndex] = useState(0);
  const [errorCount, setErrorCount] = useState(0);
  const [elapsedTime, setElapsedTime] = useState(0);
  const [showResults, setShowResults] = useState(false);
  const [language, setLanguage] = useState('chinese');
  const [length, setLength] = useState('long');
  const [totalScore, setTotalScore] = useState(0);
  const [usedTexts, setUsedTexts] = useState([]);

  const handleStartGame = () => {
    const newText = getRandomText();
    setGameStarted(true);
    setGameText(newText);
    setGameIndex(gameIndex + 1);
    setShowResults(false);
    setUsedTexts([...usedTexts, newText]);
  };

  const handleSubmit = (errors, time) => {
    setErrorCount(errors);
    setElapsedTime(time);

    const wordCount = gameText.length;
    const allowedTime = wordCount * 0.5;

    let score = 0;

    if (time <= allowedTime) {
      score = 3;
    } else if (time <= allowedTime + 5) {
      score = 2;
    } else if (time <= allowedTime + 10) {
      score = 1;
    } else if (time <= allowedTime + 20) {
      score = 0.5;
    }

    score -= errors;
    score = Math.max(score, 0); // 確保分數不會是負數

    setTotalScore(totalScore + score);
    setShowResults(true);
  };

  const handleNextLevel = () => {
    const newText = getRandomText();
    setGameText(newText);
    setGameStarted(true);
    setShowResults(false);
    setUsedTexts([...usedTexts, newText]);
  };

  const handleExitGame = () => {
    if (window.confirm('你確定要離開遊戲嗎？這會清除當前成績。')) {
      setGameStarted(false);
      setShowResults(false);
      setGameText('');
      setGameIndex(0);
      setErrorCount(0);
      setElapsedTime(0);
      setTotalScore(0);
      setUsedTexts([]);
    }
  };

  const getRandomText = () => {
    let selectedData;
    if (language === 'english') {
      selectedData = dataEng;
    } else {
      selectedData = dataCh;
    }

    let selectedTexts;
    if (length === 'short') {
      selectedTexts = selectedData.short;
    } else if (length === 'medium') {
      selectedTexts = selectedData.medium;
    } else {
      selectedTexts = selectedData.long;
    }

    const remainingTexts = selectedTexts.filter(text => !usedTexts.includes(text));
    if (remainingTexts.length === 0) {
      setUsedTexts([]);
      return selectedTexts[Math.floor(Math.random() * selectedTexts.length)];
    } else {
      return remainingTexts[Math.floor(Math.random() * remainingTexts.length)];
    }
  };

  return (
    <div className="App">
      {!gameStarted && !showResults && <GamePage onStartGame={handleStartGame} setLanguage={setLanguage} setLength={setLength} />}
      {gameStarted && !showResults && <TypingGame text={gameText} onSubmit={handleSubmit} />}
      {showResults && (
        <div>
          <h2>測驗結果</h2>
          <p>錯誤字數： {errorCount}</p>
          <p>花費時間：{elapsedTime} seconds</p>
          <p>錯誤率： {(errorCount / gameText.length * 100).toFixed(2)}%</p>
          <p>當前得分：{totalScore} 分</p>
          <button onClick={handleNextLevel}>Next Level</button>
          <button onClick={handleExitGame}>Exit Game</button>
        </div>
      )}
    </div>
  );
}

export default App;
