// TypingGame.js
import React, { useState, useEffect } from 'react';
import './TypingGame.css'; // 引入 CSS 文件

const TypingGame = ({ text, onSubmit }) => {
  const [inputText, setInputText] = useState('');
  const [startTime, setStartTime] = useState(Date.now()); // 开始计时
  const [score, setScore] = useState(0);
  const [errors, setErrors] = useState(0);
  const [elapsedTime, setElapsedTime] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setElapsedTime((Date.now() - startTime) / 1000);
    }, 1000);

    return () => clearInterval(timer); // 清除定时器
  }, [startTime]);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'Enter') {
        handleSubmit();
      }
    };
    document.addEventListener('keydown', handleKeyDown);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, [inputText, errors]);

  const handleChange = (e) => {
    const typedText = e.target.value;
    setInputText(typedText);

    // 计算错误数量
    let errorCount = 0;
    for (let i = 0; i < typedText.length; i++) {
      if (typedText[i] !== text[i]) {
        errorCount++;
      }
    }

    setErrors(errorCount);
  };

  const handleSubmit = () => {
    const endTime = Date.now();
    const elapsedTime = (endTime - startTime) / 1000; // 以秒计

    const totalErrors = text.length - inputText.length + errors;
    const errorRate = ((totalErrors) / text.length) * 100;

    setScore(Math.round(100 - errorRate));
    onSubmit(totalErrors, elapsedTime);
  };

  return (
    <div className="typing-game-container">
      <div className="info-container">
        <p className="timer">计时器：{Math.floor((Date.now() - startTime) / 1000)}秒</p>
        <p className="score">计分板：{score}分</p>
      </div>
      <div className="text-input-container">
        <p className="text">{text}</p>
        <input className="input" type="text" value={inputText} onChange={handleChange} />
        <button className="submit-button" onClick={handleSubmit}>Submit</button>
      </div>
    </div>
  );
};

export default TypingGame;
