import React, { useState, useEffect, useRef } from 'react';
import './TypingGame.css'; // 引入 CSS 文件

const TypingGame = ({ text, onSubmit, onNextLevel }) => {
  const [inputText, setInputText] = useState('');
  const [startTime, setStartTime] = useState(Date.now()); // 開始計時
  const [score, setScore] = useState(0);
  const [errors, setErrors] = useState(0);
  const [elapsedTime, setElapsedTime] = useState(0);
  const inputRef = useRef(null);

  useEffect(() => {
    const timer = setInterval(() => {
      setElapsedTime((Date.now() - startTime) / 1000);
    }, 1000);

    return () => clearInterval(timer); // 清除定時器
  }, []);

  useEffect(() => {
    inputRef.current.focus(); // 將焦點設置到輸入框上
  }, []);

  const handleChange = (e) => {
    const typedText = e.target.value;
    setInputText(typedText);

    // 計算錯誤數量
    let errorCount = 0;
    for (let i = 0; i < typedText.length; i++) {
      if (typedText[i] !== text[i]) {
        errorCount++;
      }
    }

    setErrors(errorCount);
  }

  const handleSubmit = () => {
    const endTime = Date.now();
    const elapsedTime = (endTime - startTime) / 1000; // in seconds

    const totalErrors = text.length - inputText.length + errors;
    const errorRate = ((totalErrors) / text.length) * 100;

    let bonusScore = 0;
    if (elapsedTime <= text.length * 0.5) {
      bonusScore += 30;
    } else if (elapsedTime > text.length * 0.5 && elapsedTime <= text.length * 0.5 + 5) {
      bonusScore += 20;
    } else if (elapsedTime > text.length * 0.5 + 5 && elapsedTime <= text.length * 0.5 + 10) {
      bonusScore += 10;
    } else if (elapsedTime > text.length * 0.5 + 10 && elapsedTime <= text.length * 0.5 + 20) {
      bonusScore += 5;
    }

    setScore(Math.round(100 - errorRate) + bonusScore);
    onSubmit(totalErrors, elapsedTime);
  }

  const handleNextLevel = () => {
    setInputText(''); // 清空輸入框
    setStartTime(Date.now()); // 重新計時
    setElapsedTime(0); // 重置計時器
    setErrors(0); // 重置錯誤數量
    setScore(0); // 重置分數
    setGameText(getRandomText()); // 獲取下一篇文章
  }


  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSubmit(); // 在按下 Enter 時提交
    }
  }

  return (
    <div className="typing-game-container">
      <div className="info-container">
        <p className="timer">計時器：{Math.floor((Date.now() - startTime) / 1000)}秒</p>
        <p className="score">計分板：{score}分</p>
      </div>
      <div className="text-input-container">
        <p className="text">{text}</p>
        <input
          className="input"
          type="text"
          value={inputText}
          onChange={handleChange}
          onKeyPress={handleKeyPress}
          ref={inputRef} // 將 ref 設置為 inputRef
        />
        <button className="submit-button" onClick={handleSubmit}>Submit</button>
      </div>
    </div>
  );
}

export default TypingGame;
