// src/App.js
import React, { useState } from 'react';
import GamePage from './components/GamePage';
import TypingGame from './components/TypingGame';
import dataEng from './dataEng.json';
import dataCh from './dataCh.json';

function App() {
  const [gameStarted, setGameStarted] = useState(false);
  const [gameText, setGameText] = useState('');
  const [gameIndex, setGameIndex] = useState(0);
  const [errorCount, setErrorCount] = useState(0);
  const [elapsedTime, setElapsedTime] = useState(0);
  const [showResults, setShowResults] = useState(false);
  const [language, setLanguage] = useState('english');
  const [length, setLength] = useState('short');

  const handleStartGame = () => {
    setGameStarted(true);
    setGameText(getRandomText());
    setGameIndex(gameIndex + 1);
    setShowResults(false);
  }

  const handleSubmit = (errors, time) => {
    setErrorCount(errors);
    setElapsedTime(time);
    setShowResults(true);
  }

  const handleNextLevel = () => {
    setGameStarted(false);
    setShowResults(false);
  }

  const getRandomText = () => {
    const selectedData = language === 'english' ? dataEng : dataCh;
    return selectedData[length][Math.floor(Math.random() * selectedData[length].length)];
  }

  return (
    <div className="App">
      {!gameStarted && !showResults && <GamePage onStartGame={handleStartGame} setLanguage={setLanguage} setLength={setLength} />}
      {gameStarted && !showResults && <TypingGame text={gameText} onSubmit={handleSubmit} onNextLevel={handleNextLevel} />}
      {showResults && (
        <div>
          <h2>Results</h2>
          <p>Error Count: {errorCount}</p>
          <p>Elapsed Time: {elapsedTime} seconds</p>
          <p>Error Rate: {(errorCount / gameText.length * 100).toFixed(2)}%</p>
          <button onClick={handleNextLevel}>Next Level</button>
        </div>
      )}
    </div>
  );
}

export default App;
