// src/components/ResultPanel.js
import React from 'react';

const ResultPanel = ({ errorCount, elapsedTime, onSubmitAgain }) => {
  const errorRate = (errorCount / elapsedTime) * 100;

  return (
    <div>
      <h2>測驗結果</h2>
      <p>錯誤字數： {errorCount}</p>
      <p>花費時間：{elapsedTime} seconds</p>
      <p>錯誤率： {(errorCount / gameText.length * 100).toFixed(2)}%</p>
      <p>本次得分：{lastScore} 分</p> 
      <button className="next-level-button" onClick={handleNextLevel}>下一關</button>
      <button className="exit-button" onClick={handleExitGame}>離開遊戲</button>
    </div>

  );
}

export default ResultPanel;
