// src/TypingEffect.js
import React, { useState, useEffect } from 'react';
import Typical from 'react-typical';
import './TypingEffect.css';

const TypingEffect = ({ errorCount, elapsedTime, errorRate, lastScore, onNextLevel, onExitGame }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const messages = [
    `錯誤字數：${errorCount}`,
    `花費時間：${elapsedTime} 秒`,
    `錯誤率：${errorRate}%`,
    `本次得分：${lastScore} 分`,
    '是否進入下一關？'
  ];

  useEffect(() => {
    if (currentIndex < messages.length - 1) {
      const timeout = setTimeout(() => setCurrentIndex(currentIndex + 1), 3000);
      return () => clearTimeout(timeout);
    }
  }, [currentIndex]);

  return (
    <div className="typing-effect-container">
      {messages.slice(0, currentIndex + 1).map((msg, index) => (
        <div key={index} className="typing-effect-message">
          <Typical steps={[msg, 2000]} wrapper="p" />
        </div>
      ))}
      {currentIndex === messages.length - 1 && (
        <div className="typing-effect-options">
          <button onClick={onNextLevel}>是</button>
          <button onClick={onExitGame}>否</button>
        </div>
      )}
    </div>
  );
};

export default TypingEffect;
