// TypingGame.js
import React, { useState, useEffect } from 'react';
import './TypingGame.css'; // 引入 CSS 文件

const TypingGame = ({ text, onSubmit, onNextLevel }) => {
  const [inputText, setInputText] = useState('');
  const [startTime, setStartTime] = useState(Date.now()); // 現在就開始計時
  const [score, setScore] = useState(0);
  const [elapsedTime, setElapsedTime] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setElapsedTime((Date.now() - startTime) / 1000);
    }, 1000);

    return () => clearInterval(timer);
  }, [startTime]);

  const handleChange = (e) => {
    setInputText(e.target.value);
  }

  const handleSubmit = () => {
    const endTime = Date.now();

    let errors = 0;
    for (let i = 0; i < inputText.length; i++) {
      if (inputText[i] !== text[i]) {
        errors++;
      }
    }
    const errorRate = (errors / text.length) * 100;
    const speed = Math.round(((text.length - errors) / elapsedTime) * 60);

    setScore(Math.round((100 - errorRate) * speed * 0.01));
    onSubmit(errors, elapsedTime);
  }

  const handleNextLevel = () => {
    setInputText('');
    setStartTime(Date.now()); // 開始下一關時重新計時
    onNextLevel();
  }

  return (
    <div className="typing-game-container">
      <div className="info-container">
        <p className="timer">Time: {Math.floor(elapsedTime)}s</p>
        <p className="score">Score: {score}</p>
      </div>
      <div className="text-input-container">
        <p className="text">{text}</p>
        <input className="input" type="text" value={inputText} onChange={handleChange} />
        <button className="submit-button" onClick={handleSubmit}>Submit</button>
      </div>
      <button className="next-level-button" onClick={handleNextLevel}>Next Level</button>
    </div>
  );
}

export default TypingGame;
