import './App.css';
import React, { useState } from 'react';
import GamePage from './components/GamePage';
import TypingGame from './components/TypingGame';
import ResultPanel from './components/ResultPanel';
import data from './data.json';


function App() {
  const [gameStarted, setGameStarted] = useState(false);
  const [gameText, setGameText] = useState('');
  const [showResults, setShowResults] = useState(false);
  const [errorCount, setErrorCount] = useState(0);
  const [elapsedTime, setElapsedTime] = useState(0);

  const handleStartGame = () => {
    setGameStarted(true);
    setGameText(getRandomText());
    setShowResults(false);
  }

  const handleSubmit = (inputText, time) => {
    let errors = 0;
    for (let i = 0; i < inputText.length; i++) {
      if (inputText[i] !== gameText[i]) {
        errors++;
      }
    }
    setErrorCount(errors);
    setElapsedTime(time);
    setShowResults(true);
  }

  const handlePlayAgain = () => {
    setGameStarted(false);
    setShowResults(false);
  }

  const getRandomText = () => {
    const length = Math.floor(Math.random() * 3) + 1; // Random length: 1, 2, or 3
    const randomIndex = Math.floor(Math.random() * data[length].length);
    return data[length][randomIndex];
  }

  return (
    <div className="App">
      {!gameStarted && !showResults && <GamePage onStartGame={handleStartGame} />}
      {gameStarted && !showResults && <TypingGame text={gameText} onSubmit={handleSubmit} />}
      {showResults && <ResultPanel errorCount={errorCount} elapsedTime={elapsedTime} onSubmitAgain={handlePlayAgain} />}
    </div>
  );
}

export default App;
