// src/TypingEffect.js
import React, { useState, useEffect } from 'react';
import Typical from 'react-simple-typical';
import './TypingEffect.css';

const TypingEffect = ({ errorCount, elapsedTime, errorRate, lastScore, onNextLevel, onExitGame }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [userInput, setUserInput] = useState('');
  const messages = [
    { text: `錯誤字數：${errorCount}`, sender: '系統' },
    { text: `花費時間：${elapsedTime} 秒`, sender: '系統' },
    { text: `錯誤率：${errorRate}%`, sender: '系統' },
    { text: `本次得分：${lastScore} 分`, sender: '系統' },
    { text: '是否進入下一關？請輸入「是」或「否」', sender: '系統' }
  ];

  useEffect(() => {
    if (currentIndex < messages.length - 1) {
      const timeout = setTimeout(() => setCurrentIndex(currentIndex + 1), 3000);
      return () => clearTimeout(timeout);
    }
  }, [currentIndex]);

  const handleInputChange = (e) => {
    setUserInput(e.target.value);
  };

  const handleUserInputSubmit = () => {
    if (userInput.trim() === '是') {
      onNextLevel();
    } else if (userInput.trim() === '否') {
      onExitGame();
    } else {
      alert('請輸入有效的選項：「是」或「否」');
    }
  };

  return (
    <div className="typing-effect-container">
      {messages.slice(0, currentIndex + 1).map((msg, index) => (
        <div key={index} className="message-bubble">
          <p className="sender">{msg.sender}</p>
          <Typical steps={[msg.text, 2000]} wrapper="p" />
        </div>
      ))}
      {currentIndex === messages.length - 1 && (
        <div className="user-input-container">
          <input
            type="text"
            value={userInput}
            onChange={handleInputChange}
            placeholder="請輸入「是」或「否」"
          />
          <button onClick={handleUserInputSubmit}>提交</button>
        </div>
      )}
    </div>
  );
};

export default TypingEffect;
